(function($) {

	'use strict';

	jQuery(document).ready(function () {
		
		// object fit

		objectFitImages();

		// header

		$(window).scroll(function(){

			var header = $('header.header--front');
			var scroll = $(window).scrollTop();
		
			if (scroll >= 1) {
				header.addClass('header--fixed');
				$(".header-logo__img").attr("src","img/svg/logo-dark.svg");
			}

			else {
                header.removeClass('header--fixed');
                $(".header-logo__img").attr("src","img/svg/logo.svg");
			}

        });
        
    });

}(jQuery));
